mapper.select:
SELECT COUNT(DISTINCT r.r_id) AS cnt
FROM recruitment r
JOIN company_member c ON r.cm_id = c.cm_id
LEFT JOIN duty d ON r.d_id = d.d_id
LEFT JOIN industry ind ON r.ind_id = ind.ind_id
LEFT JOIN education e ON r.e_id = e.e_id
LEFT JOIN employment_type et ON r.et_id = et.et_id
LEFT JOIN working_area wa ON r.wa_id = wa.wa_id
LEFT JOIN re_career rec ON r.rec_id = rec.rec_id
LEFT JOIN recruitment_field rf ON r.r_id = rf.r_id

    WHERE 1=1
    
        AND r.d_id = #{d_id}
    
    
        AND r.ind_id = #{ind_id}
    
    
        AND r.e_id = #{e_id}
    
    
        AND r.et_id = #{et_id}
    
    
        AND r.wa_id = #{wa_id}
    
    
        AND r.rec_id = #{rec_id}
    
    
        AND 
        
            
                c.cm_co_name LIKE '%' || #{keyword} || '%'
            
            
                r.r_title LIKE '%' || #{keyword} || '%'
            
        
    


    AND c.cm_co_type = #{cm_co_type}

SELECT cm_co_name, cm_id, r_title, r_id, r_sal, r_end_date, LISTAGG(rf_field, ', ') WITHIN GROUP (ORDER BY rf_field) AS rf_field, d_name, ind_name, education, et_name, wa_name, rec_name, cm_co_type FROM ( SELECT c.cm_co_name, c.cm_id, r.r_title, r.r_id, r.r_sal, r.r_end_date, rf.rf_field, c.cm_co_type, d.d_name, ind.ind_name, e.education, et.et_name, wa.wa_name, rec.rec_name, DENSE_RANK() OVER (ORDER BY r.r_id DESC) AS rnum FROM recruitment r JOIN company_member c ON r.cm_id = c.cm_id JOIN recruitment_field rf ON r.r_id = rf.r_id LEFT JOIN duty d ON r.d_id = d.d_id LEFT JOIN industry ind ON r.ind_id = ind.ind_id LEFT JOIN education e ON r.e_id = e.e_id LEFT JOIN employment_type et ON r.et_id = et.et_id LEFT JOIN working_area wa ON r.wa_id = wa.wa_id LEFT JOIN re_career rec ON r.rec_id = rec.rec_id
WHERE 1=1
AND r.d_id = #{d_id}
AND r.ind_id = #{ind_id}
AND r.e_id = #{e_id}
AND r.et_id = #{et_id}
AND r.wa_id = #{wa_id}
AND r.rec_id = #{rec_id}
AND
c.cm_co_name LIKE '%' || #{keyword} || '%'
r.r_title LIKE '%' || #{keyword} || '%'
AND cm_co_type = #{ cm_co_type }
) numbered_recruitment WHERE rnum BETWEEN #{startNum} AND #{endNum} GROUP BY cm_co_name, cm_id, r_title, r_id, r_sal, r_end_date, d_name, ind_name, education, et_name, wa_name, rec_name,cm_co_type

select a_id, r_id, mr_id, m_id, a_condition
from application
where m_id = #{ m_id } and r_id = #{ r_id }

select m_tel, m_email
from member
where m_id = #{ m_id }
